const unsigned int ColorMatchingFunctions::MINIMUM_WAVELENGTH = 380;
const unsigned int ColorMatchingFunctions::MAXIMUM_WAVELENGTH = 780;

CIEXYZColor ColorMatchingFunctions::Apply(Illuminant referenceLight, const LightSpectrum& rReflectanceSpectrum)
{
	float X, Y, Z, W;

	// range check
	if (rReflectanceSpectrum.GetInitialWavelength() < MINIMUM_WAVELENGTH || rReflectanceSpectrum.GetFinalWavelength() > MAXIMUM_WAVELENGTH)
	{
		//THROW_EXCEPTION(Exception, "Light spectrum out of range");
		Exception exception("Light spectrum out of range");
		exception.SetFunction(__FUNCTION__);
		exception.SetFile(__FILE__);
		exception.SetLine(__LINE__);
		throw exception;
	}

	X = Y = Z = W = 0;
	unsigned int precision = rReflectanceSpectrum.GetPrecision();
	for (unsigned int i = rReflectanceSpectrum.GetInitialWavelength(), c = 0; i < rReflectanceSpectrum.GetFinalWavelength(); i += precision, c++)
	{
		float xBar = xBarFunction[i - MINIMUM_WAVELENGTH];
		float yBar = yBarFunction[i - MINIMUM_WAVELENGTH];
		float zBar = zBarFunction[i - MINIMUM_WAVELENGTH];
		// TODO: referenceLight
		float Lw = D65Illuminance[i - MINIMUM_WAVELENGTH];
		float betha = rReflectanceSpectrum[c];
		X += Lw * betha * xBar;
		Y += Lw * betha * yBar;
		Z += Lw * betha * zBar;
		W += Lw * yBar;
	}

	X /= W;
	Y /= W;
	Z /= W;

	return CIEXYZColor(X, Y, Z);
}
